import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import StarRating from "../components/StarRating";

export default function Products() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [expanded, setExpanded] = useState(null); // id товара, который раскрыт

  useEffect(() => {
    fetch("https://fakestoreapi.com/products")
      .then((res) => res.json())
      .then((data) => setItems(data))
      .catch((err) => setError(err.message))
      .finally(() => setLoading(false));
  }, []);

  const toggleExpand = (id) => {
    setExpanded(expanded === id ? null : id);
  };

  if (loading) return <div className="container">Загрузка товаров...</div>;
  if (error) return <div className="container">Ошибка: {error}</div>;

  return (
    <div className="container">
      <h2 style={{ textAlign: "center", marginBottom: "10px" }}>
        Список товаров
      </h2>
      <div className="grid">
        {items.map((item) => (
          <div
            className="card"
            key={item.id}
            style={{
              transition: "all 0.3s ease",
              transform: expanded === item.id ? "scale(1.03)" : "scale(1)",
              background: expanded === item.id ? "#f9f9f9" : "#fff",
            }}
            onClick={() => toggleExpand(item.id)}
          >
            <img src={item.image} alt={item.title} />
            <div className="title">{item.title}</div>
            <div className="price">${item.price}</div>
            <StarRating value={item.rating?.rate ?? 0} />

            {expanded === item.id && (
              <div
                style={{
                  marginTop: 8,
                  fontSize: 13,
                  color: "#444",
                  borderTop: "1px solid #ddd",
                  paddingTop: 8,
                  lineHeight: 1.4,
                }}
              >
                {item.description.length > 150
                  ? item.description.slice(0, 150) + "..."
                  : item.description}
                <div style={{ marginTop: 8 }}>
                  <Link to={`/product/${item.id}`}>
                    <button
                      className="btn"
                      onClick={(e) => e.stopPropagation()}
                    >
                      Подробнее
                    </button>
                  </Link>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
